ValidationRule.errorConditionFormula:
AND(
OR( RecordType.DeveloperName = "Sales", RecordType.DeveloperName = "Agency_Acquisition" ), ISCHANGED(StageName), NOT( $Permission.Sales_Operations), $Profile.Name <> "System Administrator",
/* Backwards Movements */ OR(
AND( ISPICKVAL(PRIORVALUE(StageName), "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet") ),
AND( ISPICKVAL(PRIORVALUE(StageName), "Currently in Contact"), OR( ISPICKVAL(StageName, "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet") )),
AND( ISPICKVAL(PRIORVALUE(StageName), "Scheduled"), OR( ISPICKVAL(StageName, "Rescheduling"), ISPICKVAL(StageName, "Currently in Contact"), ISPICKVAL(StageName, "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet"), ISPICKVAL(StageName, "Demo Held") )),
AND( ISPICKVAL(PRIORVALUE(StageName), "Demo Confirmed"), OR( ISPICKVAL(StageName, "Scheduled"), ISPICKVAL(StageName, "Currently in Contact"), ISPICKVAL(StageName, "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet") )),
AND( ISPICKVAL(PRIORVALUE(StageName), "Demo Held"), OR( ISPICKVAL(StageName, "Scheduled"), ISPICKVAL(StageName, "Rescheduling"), ISPICKVAL(StageName, "Currently in Contact"), ISPICKVAL(StageName, "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet") )),
AND( OR( ISPICKVAL(PRIORVALUE(StageName), "Pilot Definition"), ISPICKVAL(PRIORVALUE(StageName), "Offer")), OR( ISPICKVAL(StageName, "Demo Held"), ISPICKVAL(StageName, "Demo Confirmed"), ISPICKVAL(StageName, "Scheduled"), ISPICKVAL(StageName, "Rescheduling"), ISPICKVAL(StageName, "Currently in Contact"), ISPICKVAL(StageName, "Qualified Prospect"), ISPICKVAL(StageName, "Not reached yet") )),
/* Backwards Exception Demo Held/Confirmed */ AND( $Profile.Name <> "Head of Large Hunting", $Profile.Name <> "Head of Small Account Management", $Profile.Name <> "Head of Large Prospecting", ISPICKVAL(PRIORVALUE(StageName), "Demo Held"), ISPICKVAL(StageName, "Demo Confirmed") ),
/* Movement from Prospecting to Hunting Stages */ AND( OR( ISPICKVAL( PRIORVALUE (StageName), "Not reached yet"), ISPICKVAL( PRIORVALUE (StageName), "Qualified Prospect"), ISPICKVAL( PRIORVALUE (StageName), "Currently In Contact"), ISPICKVAL( PRIORVALUE (StageName), "Scheduled") ), OR( ISPICKVAL(StageName, "Demo Held") , ISPICKVAL(StageName, "Pilot Definition"), ISPICKVAL(StageName, "Offer"), ISPICKVAL(StageName, "Contract Received ") ))
) )
