ValidationRule.errorConditionFormula:
AND(
RecordType.DeveloperName = "Sales_Transactional", ISCHANGED(StageName), $Profile.Name <> "System Administrator",
/* Backwards Movements */ OR(
AND( ISPICKVAL(PRIORVALUE(StageName), "In Qualification"), ISPICKVAL(StageName, "Ready for Sales") ),
AND( ISPICKVAL(PRIORVALUE(StageName), "Pitch & Negotiation"), OR( ISPICKVAL(StageName, "In Qualification"), ISPICKVAL(StageName, "Ready for Sales") )),
AND( ISPICKVAL(PRIORVALUE(StageName), "Offer"), OR( ISPICKVAL(StageName, "Pitch & Negotiation"), ISPICKVAL(StageName, "In Qualification"), ISPICKVAL(StageName, "Ready for Sales") ))
)
)
